import { ActionTypes } from "";

const initialState = {
    showSplitView:false,
};
export const reducers = (state = initialState, { type, payload }) => {
  switch (type) {
    case ActionTypes.Show_Split_View:
      return { ...state, showSplitView: payload };
    default:
      return state;
  }
};
